import React, {Fragment, useState} from 'react'


const Temperatura = props => {

    const [temp, setTemp] = useState(19)

    const subir =()=>{
        setTemp(temp +1)
    }

    const bajar =()=>{
        setTemp(temp -1)
    }


    return (
       <Fragment>
           <h1>Temperatura = {temp}</h1>
           <h3>{
               temp > 21 ? 'Hace Calor' : 'Esta Fresquito'
               }</h3>
           <button clas onClick={subir}>Subir</button>
           <button onClick={bajar}>Bajar</button>

       </Fragment>
    )
}



export default Temperatura
