import React, {Fragment, useState} from 'react'

const Contador = () => {
    const [number, setNumber] = useState(0)
    const subir = ()=>{
        setNumber(number +1)
    }
    const bajar = ()=>{
        setNumber(number -1)
    }

    return (
     <Fragment>
         <h1>Contador = {number}</h1>
         <button onClick={subir}>Subir</button>
         <button onClick={bajar}>Bajar</button>


     </Fragment>

    )
}

export default Contador
